            [System.Windows.Forms.MessageBox]::Show("Please enter a Wallpaper URL.", "Oops!", 'OK', 'Information')
        }
        else {
            try {
                $WallExpress = @"
                $`ProgressPreference = 'SilentlyContinue'
                $`filename = "Wallpaper.png"
                $`WebClient = New-Object System.Net.WebClient 
                $`URL = $`WalltextBox.text
                $`File = "$`env:PROGRAMDATA\$`filename" 
    
                $`WebClient.DownloadFile($`URL, $`File)
    
                $`MyWallpaper = "$`file"
                $`code = @' 
    using System.Runtime.InteropServices; 
    namespace Win32{ 
        public class Wallpaper{
            [DllImport("user32.dll", CharSet=CharSet.Auto)] 
            static extern int SystemParametersInfo (int uAction , int uParam , string lpvParam , int fuWinIni); 
             
            public static void SetWallpaper(string thePath){ 
                SystemParametersInfo(20,0,thePath,3); 
            }
        }
    } 
'@
                Add-Type $`code 
                [Win32.Wallpaper]::SetWallpaper($`MyWallpaper)
"@
                $Output = Invoke-Expression $WallExpress
                $OutputBox.text = $Output | Out-String
            }
            catch {
                [System.Windows.Forms.MessageBox]::Show("$($Error[0].Exception.Message)", 'ERROR', 'OK', 'ERROR')
            }
        }
    })

